ph -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>最小侵襲歯科治療</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>痛みの少ない治療技術</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>先進的な歯科材料の研究と応用</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>​​</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>歯科医師免許</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>臨床歯科研究の国際認定</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>​​</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>日本語</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>英語</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->',	'クリニックについて',	'',	'inherit',	'closed',	'closed',	'',	'7-revision-v1',	'',	'',	'2023-11-21 09:01:00',	'2023-11-21 00:01:00',	'',	7,	'http://shigotoru-2023-12.local/?p=153',	0,	'revision',	'',	0),
