lassName\":\"section_staff\"} -->\n<div class=\"wp-block-columns alignfull section_staff\"><!-- wp:column {\"className\":\"section_inner\"} -->\n<div class=\"wp-block-column section_inner\"><!-- wp:columns {\"className\":\"text_outer\"} -->\n<div class=\"wp-block-columns text_outer\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"className\":\"section_title\"} -->\n<h2 class=\"wp-block-heading section_title\">最高のケアを提供</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"className\":\"section_description\"} -->\n<p class=\"section_description\">スマイルデンタルクリニックでは、熱心で経験豊富なスタッフが患者様一人ひとりに最適なケアを提供します。<br>私たちのスタッフは、それぞれの専門分野で高い技術と知識を持ち、患者様の口腔健康をサポートするため日々努力しています。<br>リラックスできる温かい雰囲気を大切にし、患者様の健康と幸福を常に第一に考えます。<br>スタッフの笑顔と優しさが、患者様に安心感を提供し、当クリニックでの治療が患者様にとって心地よい体験となることを願っています。<br>当院での出会いが、患者様の歯科治療に対する新たな一歩となることを期待しています。</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:buttons {\"className\":\"my_btn\"} -->\n<div class=\"wp-block-buttons my_btn\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link wp-element-button\">スタッフを見る</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:image {\"id\":259,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"section_img\"} -->\n<figure class=\"wp-block-image size-large section_img\"><img src=\"http://shigotoru-2023-12.local/wp-content/uploads/2023/11/23439982_m-1024x683.jpg\" alt=\"\" class=\"wp-image-259\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"full\",\"className\":\"section_access\"} -->\n<div class=\"wp-block-columns alignfull section_access\"><!-- wp:column {\"className\":\"section_inner\"} -->\n<div class=\"wp-block-column section_inner\"><!-- wp:heading {\"className\":\"section_title\"} -->\n<h2 class=\"wp-block-heading section_title\">アクセス</h2>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<iframe class=\"map\" src=\"https://snazzymaps.com/embed/357847\" width=\"100%\" height=\"600px\" style=\"border:none;\"></iframe>\n<!-- /wp:html -->\n\n<!-- wp:buttons {\"className\":\"my_btn\"} -->\n<div class=\"wp-block-buttons my_btn\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link wp-element-button\">アクセスを見る</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"align\":\"full\",\"className\":\"section_blog\"} -->\n<div class=\"wp-block-columns alignfull section_blog\"><!-- wp:column {\"className\":\"section_inner\"} -->\n<div class=\"wp-block-column section_inner\"><!-- wp:heading {\"className\":\"section_title\"} -->\n<h2 class=\"wp-block-heading section_title\">お家で出来るケア</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"className\":\"section_description\"} -->\n<p class=\"section_description\">スマイルデンタルクリニックのブログでは、家庭でできる口腔ケアや健康な食生活のアドバイス、正しい歯磨き方法など、あなたの歯を守るための役立つ情報が満載です。<br>『家庭でできる簡単な口腔ケア』のような記事を通じて、日常生活での歯の健康管理に役立つヒントを得ることができます。<br>健康な笑顔のために、ぜひチェックしてみてください。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:query {\"queryId\":10,\"query\":{\"perPage\":3,\"pages\":0,\"offset\":0,\"postType\":\"post\",\"order\":\"desc\",\"orderBy\":\"date\",\"author\":\"\",\"search\":\"\",\"exclude\":[],\"sticky\":\"\",\"inherit\":false},\"className\":\"post\"} -->\n<div class=\"wp-block-query post\"><!-- wp:post-template {\"className\":\"post_inner\"} -->\n<!-- wp:columns {\"verticalAlignment\":\"center\",\"className\":\"post_item\"} -->\n<div class=\"wp-block-columns are-vertically-aligned-center post_item\"><!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"25%\",\"className\":\"post_img_outer\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center post_img_outer\" style=\"flex-basis:25%\"><!-- wp:post-featured-image {\"isLink\":true,\"className\":\"post_img\"} /--></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\",\"width\":\"75%\",\"className\":\"post_title_outer\